    dupList = []
    listScore = []
    score = 0
    #print(s)
    if stat == 0:
        for i in range(0,len(s)):
            if s[i] not in listVowel:
                #score += 1
                list1.append(s[i])
                list2.append(i)
    else:
        for i in range(0,len(s)):
            if s[i] in listVowel:
                #score += 1
                list1.append(s[i])
